clear;% t(x)=-1*x^4+2*x^3-x+3tx=[-1,2,0,-1,3];% k(x)=3*x*ln(2*x+1)+x^2/(x+1) kx=@(x) 3*x*log(2*x+1)+x^2/(x+1);x=3;h=0.1;function [p1,p1_spr,p2,p2_spr,p3,p3_spr]=pochodna(fx,x=3,h=0.1)x1=x+h;x2=x-h;x3=x+2*h;x4=x-2*h;%Pierwsza pochodna - wyliczeniep1=(polyval(fx,x1)-polyval(fx,x2))/(2*h);%Sprawdzenie pierwszej pochodnej:p1_spr=(polyval(polyder(fx),x));%Druga pochodna - wyliczeniep2=(polyval(fx,x1)-2*polyval(fx,x)+polyval(fx,x2))/h^2;%Sprawdzenie drugiej pochodnejp2_spr=polyval(polyder(polyder(fx)),x);%Trzecia pochodna - wyliczeniep3=((polyval(fx,x3)-(2*polyval(fx,x1))+(2*polyval(fx,x2))-polyval(fx,x4))/(2*(h^3)));%Sprawdzenie trzeciej pochodnejp3_spr=polyval(polyder(polyder(polyder(fx))),x);endfunction[t1,t1_s,t2,t2_s,t3,t3_s]=(pochodna(tx));x1=x+h;x2=x-h;x3=x+2*h;x4=x-2*h;disp("t(x)=-1*x^4+2*x^3-x+3");disp("Pierwsza pochodna w punkcie");disp(t1);disp("Sprawdzenie: ");disp(t1_s);disp("t'(x)=-4*x^3+6*x^2-1");disp("Druga pochodna w punkcie");disp(t2);disp("Sprawdzenie");disp("t''(x)=12*x^2+12*x");disp(t2_s);disp("Trzecia pochodna");disp("t'''(x)=-24*x+12");disp(t3);disp("Sprawdzenie");disp(t3_s);disp("**************");disp("k(x)=3x*ln(2*x+1)+x^2/(x+1)");disp("Pierwsza pochodna w punkcie");clear t1_s t2_s t3_sk1=((kx(x1)-kx(x2))/(2*h))disp("Sprawdzenie");disp("k'(x)=((x*(8*x^2+17*x+8))/((x+1)^2*(2*x+1)))+3*log(2*x+1)");disp(((x*(8*x^2+17*x+8))/((x+1)^2*(2*x+1)))+3*log(2*x+1));disp("Druga pochodna w punkcie");k2=((kx(x1)+kx(x2)-2*kx(x))/h^2)disp("Sprawdzenie");disp("k''(x)=(2*(6*x^4+24*x^3+40*x^2+28*x+7))/((x+1)^3*(2*x+1)^2)");disp((2*(6*x^4+24*x^3+40*x^2+28*x+7))/((x+1)^3*(2*x+1)^2));disp("Trzecia pochodna w punkcie");k3=(((kx(x3)-(2*kx(x1))+(2*kx(x2))-kx(x4))/(2*(h^3))))disp("Sprawdzenie");disp("k'''(x)=-6*(4*x^5+22*x^4+56*x^3+64*x^2+34*x+7)/((x+1)^4*(2*x+1)^3)");disp(-6*(4*x^5+22*x^4+56*x^3+64*x^2+34*x+7)/((x+1)^4*(2*x+1)^3));disp("**************");disp("Dzialanie:");disp("(5*((t''(x0)-k''(x0))^2)+10)/(((t'(x0)+k'(x0))^2)+1)");wynik1=(5*((t2-k2)^2)+10)/(((t1+k1)^2)+1)disp("Zadanie dodatkowe:")disp("t'''(x0)-2*t''(x0)+3*t'(x0)-4*t(x0)");t0=@(x) -1*x^4+2*x^3-x+3;t0(x)wynik2=(t3-2*t2+3*t1-4*t0(x))